mapper.update:
UPDATE board
SET readcnt = readcnt +1
WHERE num = #{num}

mapper.select:
/*Board - detail */
SELECT num, author, title, content,
TO_CHAR(writeday, 'YYYY-MM-DD HH24:MI:SS') writeday,
readcnt, repRoot, repIndent, repStep
FROM board
WHERE num = #{num}

mapper.update:
UPDATE board SET
	title = #{title},
	content = #{content},
	writeday = sysdate

	,passwd = #{passwd}

WHERE num = #{num}
